import React, { useEffect, useState, useContext } from "react";
import { UserContext } from "../../App";
import { useParams } from "react-router-dom";
import M from "materialize-css";

const Profile = () => {
  const [mypics, setPics] = useState([]);
  const [userProfile, setProfile] = useState(null);
  const { showRate, setShowRate } = useState(true);
  const { state, dispatch } = useContext(UserContext);
  const { userid } = useParams();
  console.log(userid);
  useEffect(() => {
    fetch(`/user/${userid}`, {
      headers: {
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
    })
      .then((res) => res.json())
      .then((result) => {
        console.log(result);
        setProfile(result);
        console.log(userProfile);
      });
  }, []);

  useEffect(() => {
    var elems = document.querySelector("#collapsible");
    var instances = M.Collapsible.init(elems, {});
    var elem = document.querySelector(".tabs");
    var instance = M.Tabs.init(elem, {});

    fetch("/mypost", {
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
    })
      .then((res) => res.json())
      .then((result) => {
        setPics(result.mypost);
      });
  }, []);

  const rateUser = () => {
    fetch("/rate", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
      body: JSON.stringify({
        rateid: userid,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        dispatch({
          type: "UPDATE",
          payload: { myRating: data.myRating, rating: data.rating },
        });
        localStorage.setItem("user", JSON.stringify(data));
        setProfile((prevState) => {
          return {
            ...prevState,
            user: {
              ...prevState.user,
              rating: [...prevState.user.rating, data._id],
            },
          };
        });
        setShowRate(false);
        console.log(data);
      });
  };

  const unRateUser = () => {
    fetch("/unrate", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
      body: JSON.stringify({
        unrateid: userid,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        dispatch({
          type: "UPDATE",
          payload: { myRating: data.myRating, rating: data.rating },
        });
        localStorage.setItem("user", JSON.stringify(data));
        setProfile((prevState) => {
          const newRater = prevState.user.rating.filter(
            (item) => item !== data._id
          );
          return {
            ...prevState,
            user: {
              ...prevState.user,
              rating: newRater,
            },
          };
        });
        console.log("unrate");
        console.log(data);
      });
  };

  return (
    <>
      {
        <div style={{ maxWidth: "1000px", margin: "0px auto" }}>
          <div
            style={{
              display: "flex",
              justifyContent: "space-around",
              margin: "18px 0px",
              borderBottom: "1px solid grey",
            }}
          >
            <div>
              <img
                style={{
                  width: "180px",
                  height: "180px",
                  borderRadius: "90px",
                }}
                src={
                  userProfile
                    ? userProfile.user.photo
                    : "https://images.unsplash.com/photo-1553394966-0b093c3af063?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80"
                }
                alt="_blank"
              />
            </div>
            <div>
              <h5>{userProfile ? userProfile.user.name : ""}</h5>
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  width: "108%",
                }}
              >
                <h6>46 posts</h6>
                <h6>
                  {userProfile ? userProfile.user.rating.length : ""} rating
                </h6>
              </div>
              {showRate ? (
                <button
                  className="btn waves-effect  light-blue darken-4"
                  onClick={() => rateUser()}
                >
                  דרג אותי
                </button>
              ) : (
                <button
                  className="btn waves-effect  light-blue darken-4"
                  onClick={() => unRateUser()}
                >
                  הסר דירוג
                </button>
              )}
            </div>
          </div>

          <ul id="tabs-swipe-demo" className="tabs tabs-fixed-width">
            <li className="tab col s3">
              <a className="active" href="#test-swipe-1">
                מוצרים
              </a>
            </li>
            <li className="tab col s3">
              <a href="#test-swipe-2">ביקורות</a>
            </li>
          </ul>
          <div id="test-swipe-1" className="col s12">
            <div className="gallery">
              {mypics.map((item) => {
                return (
                  <div key={item._id} className="card profile hoverable">
                    <div className="card-image waves-effect waves-block waves-light">
                      <img
                        className="activator"
                        src={item.photo}
                        alt={item.title}
                      />
                    </div>
                    <div className="card-content">
                      <span className="card-title activator grey-text text-darken-4">
                        {item.title}
                        <i className="material-icons right">more_vert</i>
                      </span>
                      <p>
                        <a href={item.photo}>photo</a>
                      </p>
                    </div>
                    <div className="card-reveal">
                      <span className="card-title grey-text text-darken-4">
                        {item.title}
                        <i className="material-icons right">close</i>
                      </span>
                      <p>{item.body}</p>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
          <div id="test-swipe-2" className="col s12" style={{ direction: "rtl" }}>
            <div class="row">
              <form class="col s12">
                <div class="row" >
                  <div class="input-field col s6 right">
                    <i class="material-icons prefix">mode_edit</i>
                    <textarea
                      id="icon_prefix2"
                      class="materialize-textarea"
                    ></textarea>
                    <label for="icon_prefix2">כתוב ביקורת</label>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      }
    </>
  );
};

export default Profile;
