import React, {useEffect, useState, useContext} from "react";
import {UserContext} from '../../App'

const Profile = () => {
  const [mypics,setPics] = useState([])
  const [cart,setCart] = useState([])
  const {state,dispatch} = JSON.parse(JSON.stringify(useContext(UserContext))) 
  useEffect(()=>{
    fetch('/mypost',{
      headers:{
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt"),
      }
    }).then(res=>res.json())
    .then(result=>{
      setPics(result.mypost)
    })
    
  },[])

  const myCart = () => {
    fetch('/mycart',{
      headers:{
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt"),
      }
    }).then(res=>res.json())
    .then(result=>{
      console.log(result)
      // setCart(result)
    })
  }
  return (
    <div style={{ maxWidth:"550px",margin:"0px auto" }}>
      <div style={{
        display:"flex",
        justifyContent:"space-around",
        margin:"18px 0px",
        borderBottom:"1px solid grey"
      }}>
        <div>
          <img style={{width:"160px",height:"160px",borderRadius:"80px"}} 
          src="https://images.unsplash.com/photo-1569466896818-335b1bedfcce?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60"
          alt="_blank"
           />
        </div>
        <div>
          <h5>{state ? state.name : "loading"}</h5>
          <div style={{display:"flex",justifyContent:"space-between", width:"108%"}}>
            <h6>46 posts</h6>
            <h6>30 followers</h6>
            <h6>70 following</h6>
          </div>
        </div>
      </div>
      
      <div className="gallery">
        {
          mypics.map(item=>{
            return(
              <img key={item._id} className="item" src={item.photo} alt={item.title} />
            )
          })
        }
      </div>
      <form onSubmit={e=>{
                e.preventDefault()
                 myCart()
              }}>
                <button className="btn waves-effect waves-light" type="submit" name="action">סל מוצרים
                </button>
              </form>
      
    
    </div>
  )
};

export default Profile;
