import React, { useContext } from "react";
import { Link, useHistory } from "react-router-dom";
import { UserContext } from "../App";

const NavBar = () => {
  const { state, dispatch } = useContext(UserContext);
  const history = useHistory()
  const renderList = () => {
    if (state) {
      return [
        <li style={{ marginRight: "-3%" }} key={1}>
          <Link to="/profile">פרופיל</Link>
        </li>,
        <li style={{ marginRight: "-3%" }} key={2}>
          <Link to="/create">העלה מוצר</Link>
        </li>,
        <li style={{ marginRight: "-3%" }} key={3}>
          <button
            className="btn waves-effect white" style={{color:"#FC4A1A",margin:"7.5px"}}
            onClick={() => {
              localStorage.clear();
              dispatch({ type: "CLEAR" });
              history.push('/signin')
            }}
          >
            התנתק
          </button>
        </li>,
      ];
    } else {
      return [
        <li style={{ marginRight: "-3%" }} key={4}>
          <Link to="/signin">
            כניסה לחשבון
          </Link>
        </li>,
        <li style={{ marginRight: "-3%" }} key={5}>
          <Link to="/signup">
            הירשם
          </Link>
        </li>,
      ];
    }
  };
  return (
    <nav>
      <div className="nav-wrapper blue-grey darken-1">
        <Link
          to={state ? "/" : "/signin"}
          className="brand-logo right"
          style={{ marginRight: "3%" }}
        >
         Use Main! 
        </Link>
        <ul id="nav-mobile" className="left ">
          {renderList()}
        </ul>
      </div>
    </nav>
  );
};

export default NavBar;
