import React, { useContext } from "react";
import { Link, useHistory } from "react-router-dom";
import { UserContext } from "../App";

const NavBar = () => {
  const { state, dispatch } = useContext(UserContext);
  const history = useHistory()
  const renderList = () => {
    if (state) {
      return [
        <li style={{ marginRight: "-5%" }} key={1}>
          <Link to="/profile">Profile</Link>
        </li>,
        <li style={{ marginRight: "-5%" }} key={2}>
          <Link to="/create">Create Post</Link>
        </li>,
        <li style={{ marginRight: "-5%" }} key={3}>
          <button
            className="btn waves-effect white" style={{color:"#FC4A1A",margin:"7.5px"}}
            onClick={() => {
              localStorage.clear();
              dispatch({ type: "CLEAR" });
              history.push('/signin')
            }}
          >
            Signout
          </button>
        </li>,
      ];
    } else {
      return [
        <li style={{ marginRight: "-5%" }} key={4}>
          <Link to="/signin">
            <i className="material-icons">assignment_ind</i>
          </Link>
        </li>,
        <li style={{ marginRight: "-5%" }} key={5}>
          <Link to="/signup">
            <i className="material-icons">account_circle</i>
          </Link>
        </li>,
      ];
    }
  };
  return (
    <nav>
      <div className="nav-wrapper">
        <Link
          to={state ? "/" : "/signin"}
          className="brand-logo right"
          style={{ marginRight: "3%" }}
        >
          Use My Tools
        </Link>
        <ul id="nav-mobile" className="left ">
          {renderList()}
        </ul>
      </div>
    </nav>
  );
};

export default NavBar;
