import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import M from 'materialize-css'

const Signup = () => {
  const history = useHistory()
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");

  const PostData = () =>{
    if(!/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(email)){
      M.toast({html: "invalid email", classes:"#b71c1c red darken-4"})
      return
    }
    fetch("/signup",{
      method:"post",
      headers:{
        "Content-Type":"application/json"
        
      },
      body:JSON.stringify({
        name,
        password,
        email
      })
    }).then(res=>res.json())
    .then(data=>{
      if(data.error){
        M.toast({html: data.error, classes:"#b71c1c red darken-4"})
      } else {
        M.toast({html:data.message, classes:"#689f38 light-green darken-2"})
        history.push('./signin')
      }
    }).catch(err=>{
      console.log(err)
    })
  }


  return (
    <div className="mycard">
      <div className="card auth-card ">
        <h2>Use Mine!</h2>
        <i className="material-icons prefix">account_circle</i>
        <input type="text" placeholder="שם מלא" value={name} onChange={(e)=>setName(e.target.value)}/>
        <input type="text" placeholder="אימייל" value={email} onChange={(e)=>setEmail(e.target.value)}/>
        <input type="password" placeholder="סיסמא" value={password} onChange={(e)=>setPassword(e.target.value)}/>
        <button className="btn waves-effect  light-blue darken-4" onClick={()=>PostData()}>הירשם</button>
        <h5>
          <Link to="/signin">יש לך כבר חשבון קיים?</Link>
        </h5>
      </div>
    </div>
  );
};

export default Signup;
