import React, { useEffect, useState, useContext } from "react";
import { UserContext } from "../../App";
import M from "materialize-css";
import { useHistory, Link, Redirect, useParams, useLocation } from "react-router-dom";

const PaymentSuccess = () => {

  const { state, dispatch } = useContext(UserContext)
  const { url } = useParams();
  let location = useLocation();

  function getParameterByName(name, url) {
    name = name.replace(/[\[\]]/g, '\\$&');
    var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
        results = regex.exec(url);
    if (!results) return null;
    if (!results[2]) return '';
    return decodeURIComponent(results[2].replace(/\+/g, ' '));
   }

  useEffect(() => {
      console.log(location.search)
      console.log(getParameterByName('paymentId',location.search));
      console.log(getParameterByName('PayerID',location.search));
      console.log('it works')
      fetch('/success', {
        method: "get",
        headers: {
          "Content-Type": "application/json",
         // Authorization: "Bearer " + localStorage.getItem("jwt"),
        },
        // body: JSON.stringify({
        // }),
      })
        .then((res) => res.json())
        .then((link) => {
          if (link.error) {
            console.log(link.error)
          } else {
            console.log(link)
          }
        })
        .catch((err) => {
          console.log(err);
        });

    }, []);


  return (
      <div>
          <h1>התשלום בוצע בהצלחה</h1>
          <p>url</p>
      </div>
  );
};

export default PaymentSuccess;
