import React, {useEffect, useState, useContext} from "react";
import {UserContext} from '../../App'
import M from 'materialize-css'

const Profile = () => {
  const [mypics,setPics] = useState([])
  const [cart,setCart] = useState([]) 
  const {state,dispatch} = JSON.parse(JSON.stringify(useContext(UserContext))) 
  useEffect(()=>{
    fetch('/mypost',{
      headers:{
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt"),
      }
    }).then(res=>res.json())
    .then(result=>{
      setPics(result.mypost)
    })
  },[])

  useEffect(()=>{
    fetch('/mycart',{
      headers:{
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt"),
      }
    }).then(res=>res.json())
    .then(result=>{
      console.log(result.cart)
      setCart(result.cart)
    })
  },[])

  const dashboard = () =>{
    let sidenav = document.querySelector('#slide-out');
    var instance = M.Sidenav.init(sidenav, {});
    instance.open()
  }

  const goProfile = () => {
    let sidenav = document.querySelector('#slide-out');
    var instance = M.Sidenav.init(sidenav, {});
    instance.close()
  }
  
  return (
    <div style={{ maxWidth:"850px",margin:"0px auto" }}>
      <div style={{
        display:"flex",
        justifyContent:"space-around",
        margin:"18px 0px",
        borderBottom:"1px solid grey"
      }}>
        <div>
          <form action="#">
            <div class="file-field input-field add-profile-image">
              <div class="btn">
              <i class="material-icons">add</i>
                <input type="file"/>
              </div>
            </div>
          </form>
          <img style={{width:"180px",height:"180px",borderRadius:"90px"}} 
          src="https://images.unsplash.com/photo-1569466896818-335b1bedfcce?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60"
          alt="_blank"
           /> 
        </div>
        <div>
          <h5>{state ? state.name : "loading"}</h5>
          <div style={{display:"flex",justifyContent:"space-between", width:"108%"}}>
            <h6>46 Rank</h6>
            <h6>30 followers</h6>
            <h6>70 following</h6>
          </div>
          <form onSubmit={e=>{
                e.preventDefault()
                 dashboard()
              }}>
                <button style={{margin:"2%"}} className="btn waves-effect waves-light" type="submit" name="action">פעולות נוספות
                </button>
          </form>
        </div>
      </div>
      <div class="collapsible-header right">
            <i class="material-icons">filter_drama</i>
            מוצרים שהעלתי
            <span class="new badge green">{mypics.length}</span></div>
      <div class="collapsible-body"><p>Lorem ipsum dolor sit amet.</p></div>

      <ul id="slide-out" className="sidenav">
        <li>
          <div className="user-view">
          <div className="background">
            <img src="https://images.unsplash.com/photo-1569466896818-335b1bedfcce?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60"/>
          </div>
          <a href="#user"><img className="circle" src="https://images.unsplash.com/photo-1569466896818-335b1bedfcce?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60"/></a>
          <a href="#name"><span className="white-text name">{state ? state.name : "loading"}</span></a>
          <a href="#email"><span className="white-text email">{state ? state.email : "loading"}</span></a>
        </div>
        </li>
          <li><a href="#!"><i className="material-icons">home</i>בית</a></li>
          <li><a href="#!" onClick={goProfile}><i className="material-icons">person_pin</i>פרופיל אישי</a></li>
          <li><a href="#!" onClick={goProfile} ><i className="material-icons">shopping_cart</i>סל מוצרים</a></li>
      </ul>
      
      <div className="gallery">
        {
          mypics.map(item=>{
            return(
              <div key={item._id} className="card profile">
                  <div className="card-image waves-effect waves-block waves-light">
                    <img className="activator" src={item.photo} alt={item.title}/>
                  </div>
                  <div className="card-content">
                    <span className="card-title activator grey-text text-darken-4">{item.title}<i class="material-icons right">more_vert</i></span>
                    <p><a href={item.photo}>photo</a></p>
                  </div>
                  <div className="card-reveal">
                    <span className="card-title grey-text text-darken-4">{item.title}<i class="material-icons right">close</i></span>
                    <p>{item.body}</p>
                  </div>
                </div>
            )
          })
        }
      </div>
      <div style={{
        display:"flex",
        justifyContent:"space-around",
        margin:"18px 0px",
        borderBottom:"1px solid grey"
      }}></div>

      <div class="collapsible-header right ">
            <i class="material-icons">shopping_cart</i>
            סל מוצרים
            <span class="new badge green">{cart.length}</span></div>
      <div class="collapsible-body"><p>Lorem ipsum dolor sit amet.</p></div>


      <div className="gallery">
        {
          cart.map(item=>{ 
            if(item){
              return(
                <div className="card profile">
                  <div className="card-image waves-effect waves-block waves-light">
                    <img className="activator" src={item.photo} alt={item.title}/>
                  </div>
                  <div className="card-content">
                    <span className="card-title activator grey-text text-darken-4">{item.title}<i class="material-icons right">more_vert</i></span>
                    <p><a href={item.photo}>photo</a></p>
                  </div>
                  <div className="card-reveal">
                    <span className="card-title grey-text text-darken-4">{item.title}<i class="material-icons right">close</i></span>
                    <p>{item.body}</p>
                  </div>
                </div>
              ) 
            } else {
              return "";
            }
          })
        }
      </div>
    </div>
  )
};

export default Profile;
