import React, { useEffect, useState, useContext } from "react";
import { UserContext } from "../../App";
import { useParams } from "react-router-dom";

const Profile = () => {
  const [userProfile, setProfile] = useState(null);
  const { showRate, setShowRate } = useState(true);
  const { state, dispatch } = useContext(UserContext);
  const { userid } = useParams();
  console.log(userid);
  useEffect(() => {
    fetch(`/user/${userid}`, {
      headers: {
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
    })
      .then((res) => res.json())
      .then((result) => {
        console.log(result);
        setProfile(result);
        console.log(userProfile);
      });
  }, []);

  const rateUser = () => {
    fetch("/rate", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
      body: JSON.stringify({
        rateid: userid,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        dispatch({
          type: "UPDATE",
          payload: { myRating: data.myRating, rating: data.rating },
        });
        localStorage.setItem("user", JSON.stringify(data));
        setProfile((prevState) => {
          return {
            ...prevState,
            user: {
              ...prevState.user,
              rating: [...prevState.user.rating, data._id],
            },
          };
        });
        setShowRate(false);
        console.log(data);
      });
  };

  const unRateUser = () => {
    fetch("/unrate", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
      body: JSON.stringify({
        unrateid: userid,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        dispatch({
          type: "UPDATE",
          payload: { myRating: data.myRating, rating: data.rating },
        });
        localStorage.setItem("user", JSON.stringify(data));
        setProfile((prevState) => {
          const newRater = prevState.user.rating.filter(item=>item !== data._id)
          return {
            ...prevState,
            user: {
              ...prevState.user,
              rating: newRater
            },
          };
        });
        setShowRate(true);
        console.log("unrate");
        console.log(data);
      });
  };

  return (
    <>
      {userProfile ? (
        <div style={{ maxWidth: "550px", margin: "0px auto" }}>
          <div
            style={{
              display: "flex",
              justifyContent: "space-around",
              margin: "18px 0px",
              borderBottom: "1px solid grey",
            }}
          >
            <div>
              <img
                style={{
                  width: "180px",
                  height: "180px",
                  borderRadius: "90px",
                }}
                src={userProfile.user.photo}
                alt="_blank"
              />
            </div>
            <div>
              <h5>{userProfile.user.name}</h5>
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  width: "108%",
                }}
              >
                <h6>46 posts</h6>
                <h6>{userProfile.user.rating.length} rating</h6>
              </div>
              {showRate ? (
                <button
                  className="btn waves-effect  light-blue darken-4"
                  onClick={() => rateUser()}
                >
                  דרג אותי
                </button>
              ) : (
                <button
                  className="btn waves-effect  light-blue darken-4"
                  onClick={() => unRateUser()}
                >
                  הסר דירוג
                </button>
              )}
            </div>
          </div>

          {/* <div className="gallery">
        {
          userProfile.posts.map(item=>{
            return(
              <img key={item._id} className="item" src={item.photo} alt={item.title} />
            )
          })
        }
      </div>
      <div className="gallery">
        {
          userProfile.cart.map(item=>{
            return(
            <li key={item._id} >{item}</li>
            )
          })
        }
      </div> */}
        </div>
      ) : (
        <h2>Loading...</h2>
      )}
    </>
  );
};

export default Profile;
