import React, {useEffect, useState, useContext} from "react";
import {UserContext} from '../../App'
import M from 'materialize-css'

const Profile = () => {
  const [mypics,setPics] = useState([])
  const [cart,setCart] = useState([]) 
  const [url, setUrl] = useState("");
  const [image, setImage] = useState("");
  const {state,dispatch} = JSON.parse(JSON.stringify(useContext(UserContext))) 
  useEffect(()=>{
    fetch('/mypost',{
      headers:{
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt"),
      }
    }).then(res=>res.json())
    .then(result=>{
      setPics(result.mypost)
    })
  },[])

  useEffect(()=>{
    fetch('/mycart',{
      headers:{
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt"),
      }
    }).then(res=>res.json())
    .then(result=>{
      console.log(result.cart)
      setCart(result.cart)
    })
  },[])

  useEffect(() => {
    if (url) {
      fetch(`/profileimage/${state._id}`, {
        method: "post",
        headers: {
          "Content-Type": "application/json",
          "Authorization": "Bearer " + localStorage.getItem("jwt"),
        },
        body: JSON.stringify({
          pic: url,
        }),
      })
        .then((res) => res.json())
        .then((data) => {
          if (data.error) {
            M.toast({ html: data.error, classes: "#b71c1c red darken-4" });
          } else {
            uploadImage();
            M.toast({
              html: "תמונת פרופיל הועלתה בהצלחה",
              classes: "#689f38 light-green darken-2",
            });
          }
        })
        .catch((err) => {
          console.log("hey");
        });
    }
  }, [url]);

  const dashboard = () =>{
    let sidenav = document.querySelector('#slide-out');
    var instance = M.Sidenav.init(sidenav, {});
    instance.open()
    console.log(state)
  }

  const goProfile = () => {
    let sidenav = document.querySelector('#slide-out');
    var instance = M.Sidenav.init(sidenav, {});
    instance.close()
    console.log(state)
  }

  const uploadImage = (image) => {
    setImage(image);
    const data = new FormData();
    data.append("file", image);
    data.append("upload_preset", "mern-stack-project");
    data.append("cloud_name", "dyiceswks");
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    const cloudinaryURL = "https://api.cloudinary.com/v1_1/dyiceswks/image/upload";
    fetch(proxyurl + cloudinaryURL, {
      method: "post",
      body: data,
    })
      .then((res) => res.json())
      .then((data) => {
        setUrl(data.url);
        console.log(url)
      })
      .catch((err) => {
        console.log(err);
      });
  };
  
  return (
    <div style={{ maxWidth:"850px",margin:"0px auto" }}>
      <div style={{
        display:"flex",
        justifyContent:"space-around",
        margin:"18px 0px",
        borderBottom:"1px solid grey"
      }}>
        <div>
          <form  action="#">
            <div  className="file-field input-field add-profile-image">
              <div className="btn-floating">
              <i className="material-icons">add</i>
                <input type="file" onChange={(e) => uploadImage(e.target.files[0])}/>
              </div>
            </div>
          </form>
          <img style={{width:"180px",height:"180px",borderRadius:"90px"}} 
          src={state ? state.photo : "https://www.pngkit.com/png/detail/281-2812821_user-account-management-logo-user-icon-png.png"}
          alt="_blank"
           /> 
        </div>
        <div>
          <h5>{state ? state.name : "loading"}</h5>
          <div style={{display:"flex",justifyContent:"space-between", width:"108%"}}>
            <h6>46 Rank</h6>
            <h6>30 followers</h6>
            <h6>70 following</h6>
          </div>
          <form onSubmit={e=>{
                e.preventDefault()
                 dashboard()
              }}>
                <button style={{margin:"2%"}} className="btn waves-effect waves-light" type="submit" name="action">פעולות נוספות
                </button>
          </form>
        </div>
      </div>
      <div className="collapsible-header right">
            <i className="material-icons">filter_drama</i>
            המוצרים שלי
            <span className="new badge green">{mypics.length}</span></div>
      <div className="collapsible-body"><p>Lorem ipsum dolor sit amet.</p></div>

      <ul id="slide-out" className="sidenav">
        <li>
          <div className="user-view">
          <div className="background">
            <img src={state ? state.photo : "https://www.pngkit.com/png/detail/281-2812821_user-account-management-logo-user-icon-png.png"}/>
          </div>
          <a href="#user"><img className="circle" src={state ? state.photo : "https://www.pngkit.com/png/detail/281-2812821_user-account-management-logo-user-icon-png.png"}/></a>
          <a href="#name"><span className="white-text name">{state ? state.name : "loading"}</span></a>
          <a href="#email"><span className="white-text email">{state ? state.email : "loading"}</span></a>
        </div>
        </li>
          <li><a href="#!"><i className="material-icons">home</i>בית</a></li>
          <li><a href="#!" onClick={goProfile}><i className="material-icons">person_pin</i>פרופיל אישי</a></li>
          <li><a href="#!" onClick={goProfile} ><i className="material-icons">shopping_cart</i>סל מוצרים</a></li>
      </ul>
      
      <div className="gallery">
        {
          mypics.map(item=>{
            return(
              <div key={item._id} className="card profile">
                  <div className="card-image waves-effect waves-block waves-light">
                    <img className="activator" src={item.photo} alt={item.title}/>
                  </div>
                  <div className="card-content">
                    <span className="card-title activator grey-text text-darken-4">{item.title}<i className="material-icons right">more_vert</i></span>
                    <p><a href={item.photo}>photo</a></p>
                  </div>
                  <div className="card-reveal">
                    <span className="card-title grey-text text-darken-4">{item.title}<i className="material-icons right">close</i></span>
                    <p>{item.body}</p>
                  </div>
                </div>
            )
          })
        }
      </div>
      <div style={{
        display:"flex",
        justifyContent:"space-around",
        margin:"18px 0px",
        borderBottom:"1px solid grey"
      }}></div>

      <div className="collapsible-header right ">
            <i className="material-icons">shopping_cart</i>
            סל מוצרים
            <span className="new badge green">{cart.length}</span></div>
      <div className="collapsible-body"><p>Lorem ipsum dolor sit amet.</p></div>


      <div className="gallery">
