import React, {useEffect, useState, useContext} from "react";
import {UserContext} from '../../App'
import M from 'materialize-css'
import { Switch, useHistory } from "react-router-dom";

const Profile = () => {
  const [mypics,setPics] = useState([])
  const [cart,setCart] = useState([]) 
  const [url, setUrl] = useState("");
  const [image, setImage] = useState("");
  const [nav, setNav] = useState("");
  let [sum, setSum] = useState(Number);
  const history = useHistory();
  const {state,dispatch} = JSON.parse(JSON.stringify(useContext(UserContext))) 
  useEffect(()=>{
    setNav("cart");
    setSum(0);
    var elems = document.querySelector('#collapsible');
    var instances = M.Collapsible.init(elems, {});
    console.log(instances);
    
    fetch('/mypost',{
      headers:{
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt"),
      }
    }).then(res=>res.json())
    .then(result=>{
      setPics(result.mypost)
    })
  },[])

  useEffect(()=>{
    fetch('/mycart',{
      headers:{
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt"),
      }
    }).then(res=>res.json())
    .then(result=>{
      var total=0;
      result.cart.map(item=>{
        total += item.price;
      })
      setSum(total);
      console.log(result.cart)
      setCart(result.cart)
    })
  },[])

  useEffect(() => {
    if (url) {
      fetch(`/profileimage/${state._id}`, {
        method: "post",
        headers: {
          "Content-Type": "application/json",
          "Authorization": "Bearer " + localStorage.getItem("jwt"),
        },
        body: JSON.stringify({
          pic: url,
        }),
      })
        .then((res) => res.json())
        .then((data) => {
          if (data.error) {
            M.toast({ html: data.error, classes: "#b71c1c red darken-4" });
          } else {
            uploadImage();
            M.toast({
              html: "תמונת פרופיל הועלתה בהצלחה",
              classes: "#689f38 light-green darken-2",
            });
          }
        })
        .catch((err) => {
          console.log(err);
        });
    }
  }, [url]);

  const removeItem = item => {
    console.log("hit removeItem")
    fetch(`/removeitem/${state._id}`, {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt"),
      },
      body: JSON.stringify({
        item
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.error) {
          M.toast({ html: data.error, classes: "#b71c1c red darken-4" });
        } else {
          M.toast({
            html: "המוצר הוסר מעגלת המוצרים",
            classes: "#689f38 light-green darken-2",
          });
          const newCart = cart.filter(item=>{
            return item._id !== data;
          })
          setCart(newCart);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }

  const uploadImage = (image) => {
    setImage(image);
    const data = new FormData();
    data.append("file", image);
    data.append("upload_preset", "mern-stack-project");
    data.append("cloud_name", "dyiceswks");
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    const cloudinaryURL = "https://api.cloudinary.com/v1_1/dyiceswks/image/upload";
    fetch(proxyurl + cloudinaryURL, {
      method: "post",
      body: data,
    })
      .then((res) => res.json())
      .then((data) => {
        setUrl(data.url);
        console.log(url)
      })
      .catch((err) => {
        console.log(err);
      });
  };
  
  return (
    <div style={{ maxWidth:"1000px",margin:"0px auto" }}>
      <div style={{
        display:"flex",
        justifyContent:"space-around",
        margin:"18px 0px",
        borderBottom:"1px solid grey"
      }}>
        <div>
          <form  action="#">
            <div  className="file-field input-field add-profile-image">
              <div className="btn-floating">
              <i className="material-icons">add</i>
                <input type="file" onChange={(e) => uploadImage(e.target.files[0])}/>
              </div>
            </div>
          </form>
          <img style={{width:"180px",height:"180px",borderRadius:"90px"}} 
          src={state ? state.photo : "https://www.pngkit.com/png/detail/281-2812821_user-account-management-logo-user-icon-png.png"}
          alt="_blank"
           /> 
        </div>
        <div>
          <h5>{state ? state.name : "loading"}</h5>
          <h6>46<i className="material-icons">grade</i></h6>
          <button onClick={()=>{setNav("cart")}} className="btn waves-effect waves-light" type="submit" name="action">סל מוצרים</button>
          <button onClick={()=>{setNav("myItems")}} className="btn waves-effect waves-light" type="submit" name="action">מוצרים שלי</button>
        </div>
      </div>

      {
        nav === "myItems" ? 
        <div>
          <div class="card-panel right">
            <div className="collapsible-header right">
              <i className="material-icons">filter_drama</i>
              המוצרים שלי
              <span className="new badge green">{mypics.length}</span>
            </div>
            <p style={{direction:"rtl"}}></p>
          </div>
          <div className="gallery">
          {
            mypics.map(item=>{
              return(
                <div key={item._id} className="card profile">
                    <div className="card-image waves-effect waves-block waves-light">
                      <img className="activator" src={item.photo} alt={item.title}/>
                    </div>
                    <div className="card-content">
                      <span className="card-title activator grey-text text-darken-4">{item.title}<i className="material-icons right">more_vert</i></span>
                      <p><a href={item.photo}>photo</a></p>
                    </div>
                    <div className="card-reveal">
                      <span className="card-title grey-text text-darken-4">{item.title}<i className="material-icons right">close</i></span>
                      <p>{item.body}</p>
                    </div>
                  </div>
              )
            })
          }
          </div></div> :
          <div>
            <div class="card-panel right">
              <div id="collapsible" className="collapsible-header right ">
                <i className="material-icons">shopping_cart</i>
                סל מוצרים
                <span className="new badge green">{cart.length}</span></div>
              <div className="collapsible-body"><p>מוצרים לתשלום</p></div>
                <p style={{direction:"rtl"}}>סכום לתשלום: {sum}</p>
            </div>
          <div className="gallery">
          {
            cart.map(item=>{ 
              if(item){
                sum += item.price;
                return(
                  <div className="card profile">
                    <a class="btn-floating halfway-fab waves-effect waves-light red absolute">
                      <i class="material-icons" onClick={()=>{removeItem(item)}}>
                        remove
                      </i>
                    </a>
                    <div className="card-image waves-effect waves-block waves-light">
                      <img className="activator" src={item.photo} alt={item.title}/>
                    </div>
                    <div className="card-content">
                      <span className="card-title activator grey-text text-darken-4">{item.title}<i className="material-icons right">more_vert</i></span>
                      <p><a href={item.photo}>photo</a></p>
                    </div>
                    <div className="card-reveal">
                      <span className="card-title grey-text text-darken-4">{item.title}<i className="material-icons right">close</i></span>
                      <p>{item.body}</p>
                    </div>
                  </div>
                ) 
              } else {
                return "";
              }
            })
          }
          </div></div>
      }


      
    </div>
  )
};

export default Profile;
